import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

function CreateProduct(props) {
  const [name, setName] = useState();
  const [price, setPrice] = useState();
  const [quantity, setQuantity] = useState();
  const navigate = useNavigate();

  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isInvalidPrice, setIsInvalidPrice] = useState(false);
  const [isInvalidQuantity, setIsInvalidQuantity] = useState(false);

  const Submit = (e) => {
    e.preventDefault();
    setIsSubmitted(true); // Set form submission status to true
    setIsInvalidPrice(!/^[0-9]+(\.[0-9]+)?$/.test(price)); // Check if price is invalid
    setIsInvalidQuantity(!/^[0-9]+(\.[0-9]+)?$/.test(quantity));
    if (!isInvalidPrice && !isInvalidQuantity) {
      axios
        .post("http://localhost:5000/createProduct", { name, price, quantity })
        .then((result) => {
          console.log(result);
          navigate("/");
        })
        .catch((err) => console.log(err));
    }
  };
  return (
    <div
      className="d-flex vh-100  justify-content-center align-items-center"
      style={{ backgroundColor: "#eef0f1" }}
    >
      {" "}
      <div className="w-50 bg-white rounded p-3">
        <form onSubmit={Submit}>
          <h2> Ajouter produit</h2>
          <div className="mb-2">
            <label>Nom</label>
            <input
              type="text"
              placeholder="Entrez le nom du produit"
              className="form-control"
              required
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label>Prix</label>
            <input
              type="text"
              placeholder="Entrez le prix du produit"
              className="form-control"
              required
              pattern="[0-9]+(\.[0-9]+)?"
              onChange={(e) => {
                setPrice(e.target.value);
                setIsInvalidPrice(false); // Reset validation status on change
              }}
            />
            {isSubmitted && isInvalidPrice && (
              <small className="text-danger">
                Enter a valid integer or float number.
              </small>
            )}
          </div>
          <div className="mb-2">
            <label>Quantit√©</label>
            <input
              type="text"
              placeholder="Entrez la quantit√© disponible du produit"
              className="form-control"
              required
              pattern="[0-9]+(\.[0-9]+)?"
              onChange={(e) => {
                setQuantity(e.target.value);
                setIsInvalidQuantity(false); // Reset validation status on change
              }}
            />
            {isSubmitted && isInvalidQuantity && (
              <small className="text-danger">
                Enter a valid integer or float number.
              </small>
            )}
          </div>
          <button className="btn btn-success">Valider</button>
          <Link to="/" className="btn btn-danger">
            Annuler
          </Link>
        </form>
      </div>
    </div>
  );
}

export default CreateProduct;
